import React from 'react';
import PropTypes from 'prop-types';

//Material components
import Grid from '@material-ui/core/Grid';

//Styles
import './testimonial.css';

class Card extends React.Component {
    render() {
        console.log(this.props)
        return (
            <div className="card" width={this.props.widthSetting}>
                <h2>{this.props.title}</h2>
                <div className="line" style={{ backgroundColor: this.props.bgc }}></div>
                <p>{this.props.content}</p>
            </div>
        )
    }
}

class Link extends React.Component {
    render() {
        return (
            <a href={this.props.href}>{this.props.text}</a>
        )
    }
}

class Testimonials extends React.Component {
    constructor() {
        super()
        this.state = {
            yellow: '#fcd000',
            blue: '#0ebeff',
            green: '#47cf73',
            purple: '#ae63e4',
            pink: '#ff0080'
        }
    }
    render() {
        return (
            <div className="container-2">
                <h2 className="home-text11 HeadingTwo" style={{ paddingBottom: '20px' }}>
                    <span className="home-text12">Testimonials from Experienced People</span>
                </h2>
                <div className='container'>
                    <Card title="Jyotsna" bgc={this.state.yellow} content="Hum isse pehle itnii dusht insaan se to mile hi nhi the. Na chain se ghoomne deti hai, na khud sath chalti hai ghumne, jeena haraam ki hui hai!" />
                    <Card title="Lalitha" bgc={this.state.green} content="Angulam nadavakunda,
                            Payaname cheripomanaana,
                            Amrutham pakanunna,
                            Vishamulaaga chusaana,

                            Yenti.. Yenti.. Yenti..
                            Ee kotha varasa,
                            Nakke teliyani nanne,
                            Nenu kalisa" />
                    <Card title="Isha" bgc={this.state.purple} widthSetting="600px"
                        content="A toast to that one college friend who became one of my closest friends after coming out of college ....â£ï¸
                        From doing reels on 'kashmir tu mai kanyakumari' to making me try all the delicaies possible in a single go, weâ€™ve done it all.
                        There hasn't been a day when we've meet and not had a blast..... It has been and amazing with my favorite trio 'Josritha' ðŸ˜.  
                        Wish you many many happy returns of the day. Badhaiiyaan ji badhaiyaan...for completing  a quater of your life, may your next quaters be filled with more happiness than ever." />
                    <Card title="Sristi" bgc={this.state.pink} content="Sochti hu kya bolna h" />
                </div>
            </div>
        )
    }
}

export default Testimonials;
